import { Component, OnInit } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { HttpClient } from '@angular/common/http';
import { Customer } from '../models/customer.model'; // Adjust the path according to your project structure

@Component({
  selector: 'app-customer-edit',
  templateUrl: './customer-edit.component.html',
  styleUrls: ['./customer-edit.component.css']
})
export class CustomerDetailComponent implements OnInit {
  customerId: number | null = null;
  customer: Customer | null = null;

  constructor(private route: ActivatedRoute, private http: HttpClient) { }

  ngOnInit() {
    this.route.paramMap.subscribe(params => {
      this.customerId = +params.get('id')!;
      this.getCustomerDetails(this.customerId);
    });
  }

  getCustomerDetails(id: number) {
    this.http.get<Customer>(`https://localhost:7007/api/customers/${id}`).subscribe(
      data => this.customer = data,
      error => console.error('Error fetching customer details:', error)
    );
  }

  submitForm() {
    if (this.customer) {
      this.http.put(`https://localhost:7007/api/customers/${this.customer.customerId}`, this.customer).subscribe(
        response => {
          console.log('Customer updated:', response);
          // Optionally navigate back to customer list or show a success message
        },
        error => console.error('Error updating customer:', error)
      );
    }
  }
}
