import { Component, OnInit } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { CustomerService } from '../../services/CustomerService';
import { CustomerModel } from '../../models/CustomerModel';

@Component({
  selector: 'app-customer-new',
  templateUrl: './customer-new.component.html',
  //styleUrls: ['./customer-list.component.css']
})
export class CustomerNewComponent implements OnInit {
  customer: CustomerModel = {
    customerId: 0,
    customerName: '',
    customerEmail: '',
    customerPhoneNumber: '',
    customerGender: 1
  };

  constructor(
    private route: ActivatedRoute,
    private customerService: CustomerService,
    private router: Router
  ) { }

  ngOnInit(): void {

  }

  submitForm() {
    if (this.customer) {
      this.customerService.addCustomer(this.customer).subscribe(
        response => {
          console.log('Customer added');
          this.router.navigate(['/customer-data']); // Navigate back to customer list or show a success message
        },
        error => console.error('Error updating customer:', error)
      );
    }
  }
}
<h1>Edit Customer</h1>

<form (ngSubmit)="submitForm()">
    <div>
        <label for="name">Name:</label>
        <input type="text" id="name" name="name" [(ngModel)]="customer.customerName" required>
    </div>
    <div>
        <label for="email">Email:</label>
        <input type="email" id="email" name="email" [(ngModel)]="customer.customerEmail" required>
    </div>
    <div>
        <label for="phone">Phone Number:</label>
        <input type="tel" id="phone" name="phone" [(ngModel)]="customer.customerPhoneNumber" required>
    </div>
    <div>
        <label for="gender">Gender:</label>
        <select id="gender" name="gender" [(ngModel)]="customer.customerGender" required>
            <option [value]="1">Male</option> <!-- Use [value] to bind option value -->
            <option [value]="2">Female</option> <!-- Use [value] to bind option value -->
        </select>
    </div>
    <button type="submit">Save</button>
</form>
