// customer-list.component.ts

import { Component, OnInit } from '@angular/core';
import { CustomerModel } from '../../models/CustomerModel';
import { CustomerService } from '../../services/CustomerService';

@Component({
  selector: 'app-customer-detail',
  templateUrl: './customer-detail.component.html',
  //styleUrls: ['./customer-list.component.css']
})
export class CustomerDetailComponent implements OnInit {
  customers: CustomerModel[] = [];

  constructor(private customerService: CustomerService) { }

  ngOnInit(): void {
    this.customerService.getCustomers().subscribe(customers => {
      this.customers = customers;
    });
  }

  editCustomer(customer: CustomerModel) {
    console.log("edit customer : ", customer);
    console.log("edit customer : ", customer.customerId);
  }
}
<h1>Edit User Details</h1>

<form (ngSubmit)="onSubmit()" #userForm="ngForm">
    <div class="form-group">
        <label for="name">Name</label>
        <input type="text" class="form-control" id="name" name="name" [(ngModel)]="user.name" required>
    </div>
    <div class="form-group">
        <label for="email">Email</label>
        <input type="email" class="form-control" id="email" name="email" [(ngModel)]="user.email" required>
    </div>
    <div class="form-group">
        <label for="phoneNumber">Phone Number</label>
        <input type="text" class="form-control" id="phoneNumber" name="phoneNumber" [(ngModel)]="user.phoneNumber" required>
    </div>
    <button type="submit" class="btn btn-primary">Submit</button>
</form>
